{
    // Required. Name of the Custom Field.
    "name": "capacitor",

    // The description of the Custom Field. Used to provide further context 
    // to field and it's values.
    "description": "Enterprise-wide systematic algorithm",

    // Not currently in use. May be used to determine order of Custom Field.
    "position": "107",

    // Required. The category determines whom this Custom Field will be accessible to:
    // [
    //      "brokerage", "company_contact", "company_user", "contract", 
    //      "inventory", "project_contact", "project_user"
    // ]
    // Note: "brokerage", "company_contact" and "company_user" can only be created with
    // a company level API key.
    "category": "inventory",

    // Required. The input_type determines how the Custom Field will appear in the UI.
    // Acceptable types are: 
    // [
    //      "checkbox", "currency", "date", "date_time", "dropdown", "long_text",
    //      "measurement", "number", "percentage", "radio", "short_text"
    // ] 
    "input_type": "dropdown",

    // The selectable values of a Custom Field. Use to provide a list of options for 
    // checkbox, dropdown or radio input types
    "options": ["SDD", "District", "Azerbaijanian"],

    // Is it possible to add unique/customized options to this custom field? Used 
    // for checkbox, dropdown or radio input_types only.  
    "other_options": true,

    // Is it possible to select multiple options in a dropdown? 
    "multiple_choice": true,

    // Is it required to complete this Custom Field when submitting a form?
    "required": true,

    // The format that a measurement input_type should use. 
    // {
    //     "imperial length" => ["in", "ft", "yd", "mi"],
    //     "imperial area" => ["in²", "ft²", "yd²"],
    //     "metric length" => ["mm", "cm", "m", "km"],
    //     "metric area" => ["mm²", "cm²", "m²"]
    // }
    "value_format": "ft"
}

